<?phpnamespace SHWPortfolioCatalog\Models;use SHWPortfolioCatalog\Core\Model;class Attributes extends Model{    protected static $tableName = 'shwcatalogattribute';    protected static $primaryKey = 'id';    public function __construct($args = array()) {  }    public function addAttribute($catalog_id, $title, $ordering) {        global $wpdb;        $sql = "INSERT INTO " . $wpdb->prefix . "shwattributes (catalog_id, title, ordering) VALUES (%d , %s, %d)";        $query = $wpdb->query($wpdb->prepare($sql, $catalog_id , $title, $ordering ));        return $query;    }    public function updateOrderAttributes($attribute, $catalog_id)    {        global $wpdb;        $where = array(            'id' => $attribute['id'],            'catalog_id' => $catalog_id        );        $updatedData = array("ordering"=>$attribute['order']);        $update = $wpdb->update($wpdb->prefix . "shwattributes", $updatedData, $where);        return $update;    }    public static function getAttributes($catalog_id) {        global $wpdb;        $query = $wpdb->prepare("Select * from  ".$wpdb->prefix."shwattributes "." where catalog_id=%d order by ordering ASC", $catalog_id) ;        $allAttributes = $wpdb->get_results($query);        return $allAttributes;    }    public function removeAttribute($attributeId, $catalog_id) {        global $wpdb;        $allAttributes = $wpdb->delete($wpdb->prefix . "shwattributes", array('id' => $attributeId, "catalog_id"=>$catalog_id));        return $allAttributes;    }}