<?phpif (!defined('ABSPATH')) {	exit();}/** * @param $class string */function SHWPortfolioCatalogAutoload($class){	$prefix = 'SHWPortfolioCatalog\\';	$baseDir = __DIR__ . '/';	// does the class use the namespace prefix?	$len = strlen($prefix);	if (strncmp($prefix, $class, $len) !== 0) {		// no, move to the next registered autoloader		return;	}	$relativeClass = substr($class, $len);	$file = rtrim($baseDir, '/') . '/' . str_replace('\\', '/', $relativeClass) . '.php';	// if the file exists, require it	if (file_exists($file)) {		require $file;	}}if (version_compare(phpversion(), '5.4', '<')) {	throw new Exception('Your current version of PHP ' . phpversion() . ' is outdated. Please, update it to 5.4 and higher in order to use Showcaster.');}spl_autoload_register('SHWPortfolioCatalogAutoload');